import React from 'react';

export default function Card({ value, selected, onClick }) {
  return (
    <button
      onClick={onClick}
      className={`rounded-xl px-6 py-4 text-xl font-semibold border-2 shadow-md transition-transform transform hover:scale-105
        ${selected ? "bg-blue-600 text-white border-blue-800" : "bg-white text-gray-800 border-gray-300"}`}
    >
      {value}
    </button>
  );
}
