import React, { useState } from 'react';
import NameEntry from './components/NameEntry';
import PokerRoom from './components/PokerRoom';

function App() {
  const [name, setName] = useState("");

  return (
    <div className="min-h-screen bg-gradient-to-tr from-blue-100 to-purple-100 p-4">
      <header className="text-center text-3xl font-bold mb-6 text-indigo-900">
        OPE Plan Poker
      </header>
      {!name ? (
        <NameEntry onSubmit={setName} />
      ) : (
        <PokerRoom playerName={name} />
      )}
    </div>
  );
}

export default App;
